const { client, xml2json } = require('./client');

function getProjectList () {
    client.iam.getProjectList(function (err, data, cb) {
        console.log('=====> err:', err)
        console.log('========> data:', data)
        const d = xml2json.parser(data)
        console.log('========> 转后的json:', JSON.stringify(d))
    })
}

